contract SimpleStore {
    uint256 public value;
    address public owner;

    constructor(uint256 _value) { // 생성자
        value = _value;
        owner = msg.sender;  // 배포시에는 배포를 실행한 사람의 EOA. 
    }
    // 이후에는 컨트랙트를 실행시킨 사람의 계정

